export default function Home() {
  return (
    <div className="pt-16">
      {/* Single Full Background Image Section */}
      <div
        className="relative w-full h-[80vh] flex items-center justify-center bg-no-repeat bg-center bg-cover"
        style={{
          backgroundImage:
            "url('https://images.pexels.com/photos/4325466/pexels-photo-4325466.jpeg')", // ✅ New Yoga Image
        }}
      >
        {/* Dark overlay for text readability */}
        <div className="absolute inset-0 bg-black bg-opacity-30"></div>

        {/* Yoga Quote */}
        <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4 leading-snug">
          “Yoga is not about touching your toes,<br /> it's about what you learn on the way down.”
        </h1>
      </div>

      {/* Features Section Below */}
      <div className="max-w-6xl mx-auto py-12 px-4 grid md:grid-cols-3 gap-6">
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Certified Instructors</h3>
          <p className="mt-2 text-gray-600">Learn from experienced yoga professionals.</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Flexible Classes</h3>
          <p className="mt-2 text-gray-600">Pick sessions that match your schedule.</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Affordable Pricing</h3>
          <p className="mt-2 text-gray-600">Take care of your body without breaking the bank.</p>
        </div>
      </div>
    </div>
  );
}
