import { useState } from "react";
import { Link } from "react-router-dom";

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-white fixed top-0 left-0 w-full z-50 shadow-md">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <span className="text-2xl font-bold text-green-700">PranaVeda</span>
        </Link>

        {/* Desktop Links */}
        <div className="hidden md:flex space-x-6 font-medium">
          <Link to="/" className="text-gray-800 hover:text-green-600">Home</Link>
          <Link to="/classes" className="text-gray-800 hover:text-green-600">Classes</Link>
          <Link to="/instructors" className="text-gray-800 hover:text-green-600">Instructors</Link>
          <Link
            to="/login"
            className="bg-green-600 text-white px-4 py-1 rounded hover:bg-green-700 transition"
          >
            Login
          </Link>
        </div>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden text-gray-800 text-2xl"
          onClick={() => setIsOpen(!isOpen)}
        >
          â˜°
        </button>
      </div>

      {/* Mobile Dropdown */}
      {isOpen && (
        <div className="md:hidden bg-white px-4 py-2 shadow">
          <Link to="/" className="block py-2 text-gray-800 hover:text-green-600">Home</Link>
          <Link to="/classes" className="block py-2 text-gray-800 hover:text-green-600">Classes</Link>
          <Link to="/instructors" className="block py-2 text-gray-800 hover:text-green-600">Instructors</Link>
          <Link
            to="/login"
            className="block py-2 bg-green-600 text-white text-center rounded hover:bg-green-700"
          >
            Login
          </Link>
        </div>
      )}
    </nav>
  );
}
