import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/autoplay";
import { Autoplay } from "swiper/modules";

export default function Home() {
  return (
    <div className="pt-16">
      {/* Slider Section */}
      <div className="relative w-full h-[80vh]">
        <Swiper
          modules={[Autoplay]}
          autoplay={{ delay: 4000 }}
          loop={true}
          className="w-full h-full"
        >
          {/* Slide 1 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/3823039/pexels-photo-3823039.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4">
                “Yoga is the journey of the self, through the self, to the self.”
              </h1>
            </div>
          </SwiperSlide>

          {/* Slide 2 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/4325466/pexels-photo-4325466.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4">
                “Balance your body, calm your mind, free your spirit.”
              </h1>
            </div>
          </SwiperSlide>

          {/* Slide 3 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/2908175/pexels-photo-2908175.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4">
                “Breathe in peace, breathe out stress.”
              </h1>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>
    </div>
  );
}
