import { useState } from "react";
import { Link } from "react-router-dom";

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md fixed top-0 left-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-2xl font-extrabold text-green-600 flex items-center">
          <span className="mr-2">ðŸ§˜</span> PranaVeda
        </Link>

        {/* Desktop Links */}
        <div className="hidden md:flex space-x-6 font-medium">
          <Link to="/" className="text-gray-700 hover:text-green-600">Home</Link>
          <Link to="/classes" className="text-gray-700 hover:text-green-600">Classes</Link>
          <Link to="/instructors" className="text-gray-700 hover:text-green-600">Instructors</Link>
          <Link to="/login" className="bg-green-500 text-white px-4 py-1 rounded hover:bg-green-600">
            Login
          </Link>
        </div>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden text-gray-700 text-2xl"
          onClick={() => setIsOpen(!isOpen)}
        >
          â˜°
        </button>
      </div>

      {/* Mobile Dropdown */}
      {isOpen && (
        <div className="md:hidden bg-white px-4 py-2 shadow animate-fadeIn">
          <Link to="/" className="block py-2 text-gray-700">Home</Link>
          <Link to="/classes" className="block py-2 text-gray-700">Classes</Link>
          <Link to="/instructors" className="block py-2 text-gray-700">Instructors</Link>
          <Link to="/login" className="block py-2 bg-green-500 text-white text-center rounded">
            Login
          </Link>
        </div>
      )}
    </nav>
  );
}
