import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useLocation,
  Outlet,
} from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Classes from "./pages/Classes";
import Instructors from "./pages/Instructors";
import Auth from "./pages/Auth";

// ✅ Protect routes
function PrivateRoute({ children }) {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/auth" replace />;
}

// ✅ Layout wrapper for Navbar/Footer based on route
function LayoutWrapper() {
  const location = useLocation();
  const token = localStorage.getItem("token");
  const hideNavbar = location.pathname === "/auth";

  return (
    <>
      {token && !hideNavbar && <Navbar />}
      <Outlet />
      <Footer />
    </>
  );
}

function App() {
  const token = localStorage.getItem("token");

  return (
    <Router>
      <Routes>
        {/* ✅ Auth page outside layout */}
        <Route path="/auth" element={<Auth />} />

        {/* ✅ Layout with nested protected routes */}
        <Route element={<LayoutWrapper />}>
          <Route
            path="/"
            element={
              <PrivateRoute>
                <Home />
              </PrivateRoute>
            }
          />
          <Route
            path="/classes"
            element={
              <PrivateRoute>
                <Classes />
              </PrivateRoute>
            }
          />
          <Route
            path="/instructors"
            element={
              <PrivateRoute>
                <Instructors />
              </PrivateRoute>
            }
          />
        </Route>

        {/* ✅ Wildcard Route: Redirect unknown or empty routes */}
        <Route
          path="*"
          element={<Navigate to={token ? "/" : "/auth"} replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
