import { useState } from "react";
import { Link } from "react-router-dom";
import logo from "../assets/logo.jpg"; // ✅ Add logo in src/assets/

export default function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="absolute top-0 left-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <img src={logo} alt="PranaVeda" className="h-10 w-10 rounded-full bg-white p-1" />
          <span className="text-2xl font-bold text-white">PranaVeda</span>
        </Link>

        {/* Desktop Links */}
        <div className="hidden md:flex space-x-6 font-medium">
          <Link to="/" className="text-white hover:text-green-200">Home</Link>
          <Link to="/classes" className="text-white hover:text-green-200">Classes</Link>
          <Link to="/instructors" className="text-white hover:text-green-200">Instructors</Link>
          <Link to="/login" className="bg-green-500 text-white px-4 py-1 rounded hover:bg-green-600">
            Login
          </Link>
        </div>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden text-white text-2xl"
          onClick={() => setIsOpen(!isOpen)}
        >
          ☰
        </button>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden bg-white px-4 py-2 shadow">
          <Link to="/" className="block py-2 text-gray-700">Home</Link>
          <Link to="/classes" className="block py-2 text-gray-700">Classes</Link>
          <Link to="/instructors" className="block py-2 text-gray-700">Instructors</Link>
          <Link to="/login" className="block py-2 bg-green-500 text-white text-center rounded">
            Login
          </Link>
        </div>
      )}
    </nav>
  );
}
