import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Classes from "./pages/Classes";
import Instructors from "./pages/Instructors";
import Auth from "./pages/Auth";  // âœ… Combined Login+Register page

function PrivateRoute({ children }) {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/auth" />; // âœ… Redirect to combined auth page
}

function Layout({ children }) {
  const location = useLocation();
  const token = localStorage.getItem("token");
  const hideNavbar = location.pathname === "/auth"; // âœ… Hide navbar on auth page

  return (
    <>
      {token && !hideNavbar && <Navbar />}
      {children}
      <Footer />
    </>
  );
}

function App() {
  return (
    <Router>
      <Layout>
        <Routes>
          {/* âœ… Single Auth Page */}
          <Route path="/auth" element={<Auth />} />

          {/* ðŸ”’ Protected Routes */}
          <Route path="/" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/classes" element={<PrivateRoute><Classes /></PrivateRoute>} />
          <Route path="/instructors" element={<PrivateRoute><Instructors /></PrivateRoute>} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
