import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/autoplay";
import { Autoplay } from "swiper/modules";

export default function Home() {
  return (
    <div className="pt-16">
      {/* Top Slider Section */}
      <div className="relative w-full h-[80vh]">
        <Swiper
          modules={[Autoplay]}
          autoplay={{ delay: 4000 }}
          loop={true}
          className="w-full h-full"
        >
          {/* Slide 1 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center bg-no-repeat flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/3823039/pexels-photo-3823039.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4 leading-snug">
                “Yoga is the journey of the self, through the self, to the self.”
              </h1>
            </div>
          </SwiperSlide>

          {/* Slide 2 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center bg-no-repeat flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/4325466/pexels-photo-4325466.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4 leading-snug">
                “Balance your body, calm your mind, and free your spirit.”
              </h1>
            </div>
          </SwiperSlide>

          {/* Slide 3 */}
          <SwiperSlide>
            <div
              className="relative w-full h-[80vh] bg-cover bg-center bg-no-repeat flex items-center justify-center"
              style={{
                backgroundImage:
                  "url('https://images.pexels.com/photos/2908175/pexels-photo-2908175.jpeg')",
              }}
            >
              <div className="absolute inset-0 bg-black bg-opacity-30"></div>
              <h1 className="relative z-10 text-white text-3xl md:text-5xl font-bold text-center px-4 leading-snug">
                “Breathe in peace, breathe out stress.”
              </h1>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>

      {/* Features Section Below */}
      <div className="max-w-6xl mx-auto py-12 px-4 grid md:grid-cols-3 gap-6">
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Certified Instructors</h3>
          <p className="mt-2 text-gray-600">Learn from experienced yoga professionals.</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Flexible Classes</h3>
          <p className="mt-2 text-gray-600">Pick sessions that match your schedule.</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg text-center hover:scale-105 transition">
          <h3 className="text-xl font-semibold text-green-600">Affordable Pricing</h3>
          <p className="mt-2 text-gray-600">Take care of your body without breaking the bank.</p>
        </div>
      </div>
    </div>
  );
}
