import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Auth() {
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login/Register
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLogin) {
      // ✅ LOGIN LOGIC
      try {
        const { data: users } = await axios.get("http://localhost:5000/users");
        const user = users.find((u) => u.email === formData.email);
        if (!user) {
          alert("❌ User not registered");
          return;
        }

        // ✅ Verify password securely using backend
        const verifyRes = await axios.post("http://localhost:5000/api/verify-password", {
          plainPassword: formData.password,
          hashedPassword: user.password,
        });

        if (!verifyRes.data.valid) {
          alert("❌ Incorrect password");
          return;
        }

        // ✅ Get token with full user info
        const { data } = await axios.post("http://localhost:5000/api/set-token", {
          email: user.email,
          name: user.name,
          role: user.role || "student",
        });

        localStorage.setItem("token", data.token);
        alert("✅ Login Successful");
        navigate("/");
      } catch (err) {
        console.error(err);
        alert("❌ Error logging in");
      }
    } else {
      // ✅ REGISTER LOGIC with auto-login
      try {
        const { data } = await axios.post("http://localhost:5000/new-user", formData);
        localStorage.setItem("token", data.token);
        alert("✅ Registration successful! You are now logged in.");
        navigate("/");
      } catch (err) {
        console.error(err);
        alert("❌ Error registering user");
      }
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>{isLogin ? "Login" : "Register"}</h2>
      <form onSubmit={handleSubmit}>
        {!isLogin && (
          <input
            type="text"
            name="name"
            placeholder="Name"
            onChange={handleChange}
            required
          />
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit" style={{ marginTop: "10px" }}>
          {isLogin ? "Login" : "Register"}
        </button>
      </form>

      <p style={{ marginTop: "10px" }}>
        {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
        <button type="button" onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? "Register here" : "Login here"}
        </button>
      </p>
    </div>
  );
}
